package test;

import static org.junit.jupiter.api.Assertions.*;

import java.util.ArrayList;

import org.junit.jupiter.api.AfterEach;
import org.junit.jupiter.api.BeforeEach;
import org.junit.jupiter.api.Test;

import junit.framework.TestCase;
import model.categoria.CategoriaBean;
import model.categoria.CategoriaDAO;

class TestCategoria extends TestCase {

	CategoriaDAO dao;
	CategoriaBean categoriaEsistente;
	CategoriaBean categoriaNonEsistente;
	@BeforeEach
	protected void setUp() throws Exception {
		dao = new CategoriaDAO();
		categoriaEsistente = new CategoriaBean("TESTING","TESTING1",5,"TESTING","data:image/jpeg;base64,");
		categoriaNonEsistente = new CategoriaBean("PROVA1","PROVA1", 3, "TEST", "data:image/jpeg;base64,");
		dao.doSave(categoriaEsistente);
	}

	@Test
	void testDoSaveCategoriaEsistente() {
		assertEquals(-1, dao.doSave(categoriaEsistente));
	}
	@Test
	void testDoSaveCategorianonEsistente() {
		System.out.println("Categoria: "+categoriaEsistente.getNome());
		assertEquals(0, dao.doSave(categoriaNonEsistente));
	}

	@Test
	void testRicercaPerNome() {
		CategoriaBean categoria = new CategoriaBean();
		assertNotEquals(categoria, dao.doRetrieveByKey(categoriaEsistente.getNome()));
	}

	@Test
	void testRicercaPerTipo() {
		CategoriaBean categoria = new CategoriaBean();
		assertNotEquals(categoria, dao.doRetrieveByTipoGenerico(categoriaEsistente.getTipoGenerico()));
	}

	@Test
	void testRicercaTotale() {
		ArrayList<CategoriaBean> collection = new ArrayList<CategoriaBean>();
		assertNotEquals(collection, dao.doRetrieveAll());
	}
	
	@Test
	void testAggiornamentoCategoria() {
		assertEquals(true, dao.doUpdate(categoriaNonEsistente, categoriaNonEsistente.getNome()));
	}
	@Test
	void testEliminazioneCategoria() {
		assertEquals(true, dao.doDelete(categoriaNonEsistente.getNome()));
	}

	@Test
	void testDoRetrieveAllTipiGenerici() {
		ArrayList<CategoriaBean> collection = new ArrayList<CategoriaBean>();
		assertNotEquals(collection, dao.doRetrieveAllTipiGenerici());
	}
	@AfterEach
	protected void tearDown() throws Exception {
		dao.doDelete(categoriaNonEsistente.getNome());
		dao.doDelete(categoriaEsistente.getNome());
	}

}
